DER="true" ID="4flkdl4ivj5glqoig9qdte31kf" MODIFIED="1476038045749" TEXT="RSS (Rich Site Summary)"><node CREATED="1476038045749" FOLDER="true" ID="3m9i3ul68gjf3dftjctb3fhmmb" MODIFIED="1476038045749" TEXT="summarizes frequently updated online content in standard format."/><node CREATED="1476038045749" FOLDER="true" ID="3b5i4kro0vb5tjmn8vi8ag7cac" MODIFIED="1476038045749" TEXT="Free to read if the site has one."/><node CREATED="1476038045749" FOLDER="true" ID="68oc1tmil7bgckirjf9m5b0ggk" MODIFIED="1476038045749" TEXT="For example: news-related sites, blogs"/></node><node CREATED="1476038045749" FOLDER="true" ID="7evo1tasjpbm08fjda2j5en710" MODIFIED="1476038045749" TEXT="Web scraping"><node CREATED="1476038045749" FOLDER="true" ID="29pf2fld8bmdjli3ar8lqlqkl1" MODIFIED="1476038045749" TEXT="using software, scripts or by-hand"/><node CREATED="1476038045749" FOLDER="true" ID="7s2k2hbargqmo4bq4n16333vnv" MODIFIED="1476038045749" TEXT="extracting data from what is displayed on a page or what is contained in the HTML file."/></node></node><node CREATED="1476038045749" FOLDER="true" ID="0hi93rh3cvclabv9kcvldnl08i" MODIFIED="1476038045749" TEXT="4 -what does it look like?"><node CREATED="1476038045749" ID="4p09gc4ufag9fn6812u2ueoul3" MODIFIED="1476038045749" TEXT="Simple or atomic"><node CREATED="1476038045749" FOLDER="true" ID="29c8sajiqq22nfl8liet2mpgag" MODIFIED="1476038045749" TEXT="Numeric"><node CREATED="1476038045749" ID="7op7n5ekal2mfhq20p94uvgk53" MODIFIED="1476038045749" TEXT="integers"/><node CREATED="1476038045749" ID="2u5173euh1t6p3ao5m8hm5u444" MODIFIED="1476038045749" TEXT="float"/></node><node CREATED="1476038045749" FOLDER="true" ID="3gqjljj7m72s6laee71lv7b2dg" MODIFIED="1476038045749" TEXT="Boolean"><node CREATED="1476038045749" ID="07pubep3t5qb4b4h21uh0e264g" MODIFIED="1476038045749" TEXT="binary or true false values"/></node><node CREATED="1476038045749" FOLDER="true" ID="0g935adjaa98b6f0tk8eks2g02" MODIFIED="1476038045749" TEXT="Strings"><node CREATED="1476038045749" ID="573al8eflsnh67ql1lkiqo7q55" MODIFIED="1476038045749" TEXT="sequence of symbols"/></node></node><node CREATED="1476038045749" ID="1apdda5jprvs33tvi7vghnnv78" MODIFIED="1476038045749" TEXT="How is your data represented and stored ?"><node CREATED="1476038045749" FOLDER="true" ID="6mb1jpgu4eke7i5l78v6puopn6" MODIFIED="1476038045749" TEXT="Tabular Data"><node CREATED="1476038045749" ID="6ot4nc81195tl8dgrb9043da0a" MODIFIED="1476038045749" TEXT="dataset that is a two-dimensional table, where&#13;&#10;each row typically represents a single data record, and each&#13;&#10;column represents one type of measurement (csv, tsp, xlsx etc.)."/></node><node CREATED="1476038045749" FOLDER="true" ID="5guvgo5s7rt6idvlscmc7u9mfd" MODIFIED="1476038045749" TEXT="Structured Data"><node CREATED="1476038045750" ID="0usl1k2b42ggruci5sv8dpj5vg" MODIFIED="1476038045750" TEXT="each data record is presented in a form of a,&#13;&#10;possibly complex and multi-tiered, dictionary (json, xml etc.)"/></node><node CREATED="1476038045750" FOLDER="true" ID="6f3f4bbogblnm3b3u8p1eho0h8" MODIFIED="1476038045750" TEXT="Semistructured Data"><node CREATED="1476038045750" ID="7qp5isg09m7lnauiou1kef2vlb" MODIFIED="1476038045750" TEXT="not all records are represented by the same&#13;&#10;set of keys or some data records are not represented using the&#13;&#10;key-value pair structure."/></node></node><node CREATED="1476038045750" FOLDER="true" ID="0pq07nmpr4ribhnredm27acf1c" MODIFIED="1476038045750" TEXT="More on tabular data"><node CREATED="1476038045750" ID="5sb2uo1eja7tn9cutp9joq6lff" MODIFIED="1476038045750" TEXT="Observation"><node CREATED="1476038045750" ID="6a550j4qjd13aaidf25h0pt3hp" MODIFIED="1476038045750" TEXT="record of set of measurments"/></node><node CREATED="1476038045750" ID="54c7svtis02dmglic8i1gunqhi" MODIFIED="1476038045750" TEXT="Variable or attribute or measure"><node CREATED="1476038045750" ID="72h6uedvugcjs57vku98nitpsa" MODIFIED="1476038045750" TEXT="Quantitative variable"><node CREATED="1476038045750" ID="3us9koi0aoeo027t9sr0hscc6n" MODIFIED="1476038045750" TEXT="discrete"><node CREATED="1476038045750" ID="4iq9pe8cb206is8jr497cej8to" MODIFIED="1476038045750" TEXT="finite number of values"/><node CREATED="1476038045750" ID="4h3a0g28lbthfqnn53hrc02ado" MODIFIED="1476038045750" TEXT="number of siblings"/></node><node CREATED="1476038045750" ID="0lsa8a21c3t2han47ta37tvsjq" MODIFIED="1476038045750" TEXT="continuous"><node CREATED="1476038045750" ID="24fh9jprofeuos2k4m5qj8vgpv" MODIFIED="1476038045750" TEXT="infinite number of values"/><node CREATED="1476038045750" ID="4ni2ek0ug5cq2ke7lsff3uak76" MODIFIED="1476038045750" TEXT="Height"/></node></node><node CREATED="1476038045750" ID="3agrbn29h907f9fem34mic8dbd" MODIFIED="1476038045750" TEXT="Categorical variable"><node CREATED="1476038045750" ID="73uun5lriukc0onb2ttsvogivq" MODIFIED="1476038045750" TEXT="Qualitative"><node CREATED="1476038045750" ID="4ka1f3828ramkmgkkp6chvbvk5" MODIFIED="1476038045750" TEXT="No inherent order aming the vlaues"/></node></node></node><node CREATED="1476038045750" ID="51k1bljg7lluv03513kel9kes8" MODIFIED="1476038045750" TEXT="Dimension"><node CREATED="1476038045750" ID="4obrjjfivdgo37a5edm6bdv183" MODIFIED="1476038045750" TEXT="number of variables"/></node></node></node><node CREATED="1476038045750" FOLDER="true" ID="39kplf0b45ppvg35ubga9ft42k" MODIFIED="1476038045750" TEXT="5- is the data any good?"><node CREATED="1476038045750" ID="35ih3neo2d9sjokvoqkcdbg4iu" MODIFIED="1476038045750" TEXT="Missing values"><node CREATED="1476038045750" ID="5meh3m8vk3gqk4cf83nqkb28jg" MODIFIED="1476038045750" TEXT="how to fill"/></node><node CREATED="1476038045750" ID="792ribmj32ov9gvjv7qp598gkp" MODIFIED="1476038045750" TEXT="Wrong values"><node CREATED="1476038045750" ID="6cb0uhr9nou3nip5tr077rc2nr" MODIFIED="1476038045750" TEXT="how to"><node CREATED="1476038045750" ID="5618tiqkf41tq3opk3c98777j6" MODIFIED="1476038045750" TEXT="detect"/><node CREATED="1476038045750" ID="